//array to store layers for each feature type (depending on time) 
var mapLayerGroups = [];

//add all points from Json files
L.geoJson(all_points, {onEachFeature: onEachFeature, pointToLayer: pointToLayer});

//create different layer groups depending on time intervals
//add popup with message 
function onEachFeature(feature, featureLayer) {
	
	var customPopup = feature.properties.message;
	var custom_options =
    {
      'maxWidth': '500',
      'className' : 'custom'
    };

		featureLayer.bindPopup(customPopup, custom_options);
		featureLayer.on('mouseover', function (e) {
            this.openPopup();
        });
        featureLayer.on('mouseout', function (e) {
            this.closePopup();
        });
		
    //does layerGroup already exist? if not create it and add to map
    var lg = mapLayerGroups[feature.properties.time];

    if (lg === undefined) {
        lg = new L.layerGroup();
        //store layer
        mapLayerGroups[feature.properties.time] = lg;
    }
    //add the feature to the layer
    lg.addLayer(featureLayer);      
}

//display icon depending on the person property 
function pointToLayer(feature, latlng) {
	
	  if(feature.properties.person == "heidi" ){
		  if(feature.properties.mood == "good") {
			  return L.marker(latlng, {icon: icon_heidi_green});
		  }
		  else{
			  return L.marker(latlng, {icon: icon_heidi_red});
		  }
	  }
	  if(feature.properties.person == "klara"){
		  if(feature.properties.mood == "good") {
			  return L.marker(latlng, {icon: icon_klara_green});
		  }
		  else{
			  return L.marker(latlng, {icon: icon_klara_red});
		  }
	  }
	  if(feature.properties.person == "uncle_alp"){
		  if(feature.properties.mood == "good") {
			  return L.marker(latlng, {icon: icon_uncle_alp_green});
		  }
		  else{
			  return L.marker(latlng, {icon: icon_uncle_alp_red});
		  }
	  }
	  if(feature.properties.person == "peter"){
		  if(feature.properties.mood == "good") {
			  return L.marker(latlng, {icon: icon_peter_green});
		  }
		  else{
			  return L.marker(latlng, {icon: icon_peter_red});
		  }
	  }
	  if(feature.properties.person == "doctor"){
		  if(feature.properties.mood == "good") {
			  return L.marker(latlng, {icon: icon_doctor_green});
		  }
		  else{
			  return L.marker(latlng, {icon: icon_doctor_red});
		  }
	  }
	};

//show layer function
function showLayer(id) {
    var lg = mapLayerGroups[id];
    map.addLayer(lg); 
};

//hide layer function
function hideLayer(id) {
    var lg = mapLayerGroups[id];
    map.removeLayer(lg);   
}

