function myMethod(){

  let value = document.getElementById("slider").value;

    document.getElementById("slider").value = value - (-1);
    //Change here the step of the slider.
	
	current_time=document.getElementById("slider").value

	displayLayer(current_time);
  };
  
//define bounds for zooming the map 
var southWest = L.latLng(30, 5),
    northEast = L.latLng(70, 15),
    bounds = L.latLngBounds(southWest, northEast);

//get bounds of all markers currently added to map	
function getFeaturesInGroup() {
  var featureGroup1 = new L.FeatureGroup();
  map.eachLayer( function(layer) {
    if(layer instanceof L.Marker) {
      if(bounds.contains(layer.getLatLng())) {
		layer.addTo(featureGroup1);
      }
    }
  });
  var markers_bounds = featureGroup1.getBounds();
  return markers_bounds;
}

//function displaying only the markers belonging to the chosen time step group 
 function displayLayer(time_step){
		map.eachLayer(function (layer) {
			if (layer !== tile1) {
				if (layer !== tile2) {
				map.removeLayer(layer);
				}
			}
		});
	showLayer(time_step);
	var res = getFeaturesInGroup();
	
	//zoom to bounds
	map.flyToBounds(res, {padding: [100,100], animate:true, duration:3.3})
 }


 
 
