//add all the countries to one variable 
var countriesData = USA_polygon.concat(Austria_polygon, France_polygon, Switzerland_polygon, UK_polygon, Germany_polygon, Canada_polygon, Japan_polygon, Argentina_polygon);

//add choropleth map 
//based on: https://leafletjs.com/examples/choropleth/
function addChoroplethMap(){
	
	geojson = L.geoJson(countriesData, {
		style: style,
		onEachFeature: onEachCountry
	}).addTo(map);

//create info window 	
info.onAdd = function (map) {
    this._div = L.DomUtil.create('div', 'info'); // create a div with a class "info"
    this.update();
    return this._div;
};

//method that we will use to update the control based on feature properties passed
info.update = function (props) {
    this._div.innerHTML = '<h4>Number of Heidi movies</h4>' +  (props ?
        '<b>' + props.name + '</b><br />' + props.nr_movies + ' movies'
        : 'Hover over a country');
};

info.addTo(map);

//create legend to be displayed on the map 
legend.onAdd = function (map) {

    var div = L.DomUtil.create('div', 'info legend'),
        grades = [1,3,6,10],
        labels = [];

    // loop through our nr_movies intervals and generate a label with a colored square for each interval
    for (var i = 0; i < grades.length; i++) {
        div.innerHTML +=
            '<i style="background:' + getColor(grades[i] + 1) + '"></i> ' +
            grades[i] + (grades[i + 1] ? '&ndash;' + grades[i + 1] + '<br>' : '+');
    }
	
    return div;
};

legend.addTo(map);	
}

//get color based on number 
function getColor(d) {
    return d >= 10 ? '#BD0026' :
           d >= 6  ? '#E31A1C' :
           d >= 3  ? '#FD8D3C' :
					'#FED976'; 
}

//style the countries based on the number of movies 
function style(feature) {
    return {
        fillColor: getColor(feature.properties.nr_movies),
        weight: 2,
        opacity: 1,
        color: 'white',
        dashArray: '3',
        fillOpacity: 0.7
    };
}

//hightlight the feature: change style 
function highlightFeature(e) {
    var layer = e.target;

    layer.setStyle({
        weight: 3,
        color: '#666',
        dashArray: '',
        fillOpacity: 0.7
    });

    if (!L.Browser.ie && !L.Browser.opera && !L.Browser.edge) {
        layer.bringToFront();
    }
	
	info.update(layer.feature.properties);
}

//reset highlight of the feature 
function resetHighlight(e) {
    geojson.resetStyle(e.target);
	info.update();
}

//highlight feature on mouse hover 
function onEachCountry(feature, layer) {
    layer.on({
        mouseover: highlightFeature,
        mouseout: resetHighlight
    });
}
 
 
 
 
 
// GENERAL FUNCTIONS 
//create a reaction to the side panel 
window.addEventListener("message", receiveData, false);

//create a variable with information about current map 
var current_map

//receive reaction to the side panel 
function receiveData(evt) {
  console.log(evt.data);
  current_map = evt.data.map.name;
  changeLayer(current_map);
}

//changing layers with the changes of side panel 
function changeLayer(map_name){
	var div_slider = document.getElementById("slider-main");
	
	//set chart map: movies based on the book 
	if (map_name == "Movies based on the book") {
		map.eachLayer(function (layer) {
				map.removeLayer(layer);
		});
	map.addLayer(tile3);
	div_slider.style.visibility = 'hidden';
	map.flyTo([49.5077, 10.3192], 5);
	addChoroplethMap();
	}
	
	//set interactive map: map of feelings
	else{
	map.addLayer(tile1);
	map.addLayer(tile2);
	info.remove(map);
	legend.remove(map);
	div_slider.style.visibility = 'visible';
    document.getElementById("slider").value = "0";
	displayLayer(0);
	}
}


